class ExceptionTest extends \PHPUnit_Framework_TestCase
{

    public function testThrowException(){

        try {
            throw new Exception('Test Error');
        } catch (Exception $e){
            $this->assertInstanceOf('\Exception',$e);
            $this->assertInstanceOf('Efika\Loader\Exception',$e);
        }
    }
}
